import boto3

# Create Glue client
glue = boto3.client('glue', region_name='ap-south-1')

# Create Glue database
database_name = 'iris_database'
try:
    glue.create_database(DatabaseInput={'Name': database_name})
    print(f"Glue database '{database_name}' created successfully.")
except glue.exceptions.AlreadyExistsException:
    print(f"Glue database '{database_name}' already exists.")

# Create Glue table
response = glue.create_table(
    DatabaseName=database_name,
    TableInput={
        'Name': 'iris_table',
        'StorageDescriptor': {
            'Columns': [
                {'Name': 'id', 'Type': 'double'},
                {'Name': 'SepalLengthCm', 'Type': 'double'},
                {'Name': 'SepalWidthCm', 'Type': 'double'},
                {'Name': 'PetalLengthCm', 'Type': 'double'},
                {'Name': 'PetalWidthCm', 'Type': 'double'},
                {'Name': 'Species', 'Type': 'string'}
            ],
            'Location': f's3://assignment2irisspeciesdataset/Iris.csv',
            'InputFormat': 'org.apache.hadoop.mapred.TextInputFormat',
            'OutputFormat': 'org.apache.hadoop.hive.ql.io.HiveIgnoreKeyTextOutputFormat',
            'SerdeInfo': {
                'SerializationLibrary': 'org.apache.hadoop.hive.serde2.lazy.LazySimpleSerDe'
            }
        }
    }
)
print(response)
